import React from 'react';
import { NavLink } from 'react-router-dom';
import { FiBookOpen, FiUsers, FiGrid, FiBarChart2 } from 'react-icons/fi';

const navItems = [
  { to: '/dashboard', label: 'Dashboard', icon: FiGrid },
  { to: '/books', label: 'Browse Books', icon: FiBookOpen },
  { to: '/members', label: 'Members', icon: FiUsers },
  { to: '/reports', label: 'Reports', icon: FiBarChart2 },
];

function Sidebar() {
  return (
    <aside className="hidden md:flex md:flex-col w-64 bg-slate-900 text-slate-100 shadow-xl">
      <div className="flex items-center gap-2 px-6 py-5 border-b border-slate-800">
        <span className="inline-flex items-center justify-center w-9 h-9 rounded-xl bg-primary-600 shadow-md">
          <FiBookOpen className="text-white text-xl" />
        </span>
        <div>
          <div className="font-semibold tracking-tight">Library Admin</div>
          <div className="text-xs text-slate-400">Management System</div>
        </div>
      </div>
      <nav className="flex-1 px-3 py-4 space-y-1">
        {navItems.map(({ to, label, icon: Icon }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              [
                'flex items-center gap-3 px-3 py-2 rounded-lg text-sm font-medium transition',
                'hover:bg-slate-800/70 hover:text-white',
                isActive ? 'bg-slate-800 text-white shadow-inner' : 'text-slate-300',
              ].join(' ')
            }
          >
            <Icon className="text-lg" />
            <span>{label}</span>
          </NavLink>
        ))}
      </nav>
      <div className="px-6 py-4 text-xs text-slate-500 border-t border-slate-800">
        �a9 {new Date().getFullYear()} Library
      </div>
    </aside>
  );
}

export default Sidebar;
